<!-- Contact --> 
<div id="contact" name="section" class="bg-[#08095e]">

    <div  class=" p-3  flex items-center justify-center z-0">
        <div class="w-full md:mx-20 xl:mx-40 grid grid-cols-1 lg:grid-cols-2 flex items-center justify-center gap-20 text-gray  p-8 rounded-tl-3xl">
            <section class="flex flex-col gap-10">
                <div class="border-l-4 border-white pl-3 text-bold text-3xl touch-pan-y" data-aos="zoom-in">
                    <h2 class="text-white" >Contactame</h2>
                </div>

                <form class="w-full" data-aos="zoom-in" data-aos-delay="500">
                    <div class="flex flex-wrap -mx-3 mb-6">
                        <div class="w-full md:w-1/2 px-3 mb-6 md:mb-0">
                            <label class="block uppercase tracking-wide text-white text-xs font-bold mb-2" for="first_name">
                            Name
                            </label>
                            <input name="name" type="text" placeholder="Joe" pattern=[A-Z\sa-z]{3,20}  class="appearance-none block w-full bg-gray-200 text-gray-700 border-2 border-gray-600 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" >
                            <p class="text-red-500 text-xs italic hidden">Please fill out this field.</p>
                        </div>
                        <div class="w-full md:w-1/2 px-3">
                            <label class="block uppercase tracking-wide text-xs font-bold mb-2 text-white" for="subject">
                            Subject
                            </label>
                            <input name="subject"  type="text" placeholder="Message title" class="appearance-none block w-full bg-gray-200 text-gray-700 border-2 border-gray-600 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" >
                        </div>
                    </div>
                    <div class="flex flex-wrap -mx-3 mb-6">
                        <div class="w-full px-3">
                            <label class="block uppercase tracking-wide text-xs font-bold mb-2 text-white" for="subject">
                            Email
                            </label>
                            <input name="email"  type="email" placeholder="name@example.com" class="appearance-none block w-full bg-gray-200 text-gray-700 border-2 border-gray-600 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" >
                        </div>
                        <div class="w-full px-3">
                            <label class="block uppercase tracking-wide text-xs font-bold mb-2 text-white" for="message">
                                Your message
                            </label>
                            <textarea name="msg"  placeholder="Hello!" rows="5" class="appearance-none block w-full bg-gray-200 text-gray-700 border-2 border-gray-600 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" ></textarea>
                        
                            <p class="text-red-500 text-xs italic hidden">Please fill out this field.</p>
                        </div>
                    </div>
                    <div class="flex justify-end flex-wrap -mx-3">
                        <div class="w-full px-3 flex justify-end">
                        <a role="button" id="send" class="p-3 rounded-full bg-primary px-10 text-white hover:drop-shadow-xl hover:bg-[#7678f3] ">Enviar</a>
